document.addEventListener('scroll', function () {
  // Adjust the blur amount based on scroll position
  const scrollPosition = window.scrollY;
  const blurAmount = Math.min(scrollPosition / 10, 100); // first # is how fast, second # is how much
  document.querySelector('.blur-background').style.filter = `blur(${blurAmount}px)`;
});

document.addEventListener('scroll', function () {
  const scrollPosition = window.scrollY;
  const scale = 1.05 + scrollPosition / 5000; // Adjust the divisor for the scaling speed
  document.querySelector('.blur-background').style.transform = `scale(${scale})`;
});

// SWITCHING MENU BACKGROUNDS script.js

document.addEventListener('DOMContentLoaded', () => {
  const menuItems = document.querySelectorAll('.level-item');

  menuItems.forEach(item => {
    item.addEventListener('mouseover', (event) => {
      const bgImage = event.target.getAttribute('data-bg');
      document.body.style.backgroundImage = `url('${bgImage}')`;
    });

    item.addEventListener('mouseout', () => {
      document.body.style.backgroundImage = '';
    });
  });
});

// END SWITCHING MENU BACKGROUNDS script.js