'use strict';

document.addEventListener('DOMContentLoaded', function () {
  console.log('Hello Bulma!');

  // JavaScript for blurring the hero image on scroll
  var heroImage = document.querySelector('.hero-image');

  // window.addEventListener('scroll', function () {
  //   const scrollValue = window.scrollY;
  //   const blurValue = Math.min(scrollValue / 50, 25); // Adjust the divisor for the blur effect strength
  //   heroImage.style.filter = `blur(${blurValue}px)`;
  // });

  // window.addEventListener('scroll', function () {
  //   const scrollValue = window.scrollY;
  //   const magnifyValue = Math.min(scrollValue / 1000, 0.5); // Adjust the divisor for the magnification strength
  //   heroImage.style.transform = `scale(1 + ${magnifyValue})`;
  // });

  window.addEventListener('scroll', function () {
    var scrollValue = window.scrollY;
    var blurValue = Math.min(scrollValue / 100, 5); // Adjust the divisor for the blur effect strength
    var zoomValue = 1 + scrollValue / 500; // Adjust the divisor for the zoom effect strength
    zoomValue = Math.min(zoomValue, 3);
    // const opacityValue = Math.min(scrollValue / 500, 1);
    // const backgroundColorOpacity = Math.min(scrollValue / 1000, 0.8);


    heroImage.style.filter = 'blur(' + blurValue + 'px)';
    heroImage.style.transform = 'scale(' + zoomValue + ')';
    // heroImage.style.backgroundColor = `rgba(255, 255, 255, ${backgroundColorOpacity})`;
    // heroImage.style.opacity = opacityValue;
  });
});